YOURTABHERE:AddToggle('Bloom', {Text = 'Bloom',Default = true,Callback = function(state) if state then game:GetService"Lighting".Bloom.Enabled = true else game:GetService"Lighting".Bloom.Enabled = false end end}) Toggles.Bloom:OnChanged(function()end)
YOURTABHERE:AddToggle('SunRays', { Text = 'SunRays', Default = true, Callback = function(state) if state then game:GetService"Lighting".SunRays.Enabled = true else game:GetService"Lighting".SunRays.Enabled = false end end}) Toggles.SunRays:OnChanged(function() end)
YOURTABHERE:AddToggle('Shadows', {Text = 'Shadows',Default = true,Callback = function(state)if state then LightingService.GlobalShadows = true else LightingService.GlobalShadows = false end end})
YOURTABHERE:AddToggle('Clouds', {Text = 'Clouds',Default = true,Callback = function(state) if state then if replicatedStorage:FindFirstChild("Clouds") then replicatedStorage.Clouds.Parent = game.Workspace.Terrain end else if game.Workspace.Terrain:FindFirstChild("Clouds") then game.Workspace.Terrain.Clouds.Parent = replicatedStorage end end end})
YOURTABHERE:AddToggle('Trees', {Text = 'Trees',Default = true,Callback = function(state) pcall(function() local trees = game.Workspace.world_assets.StaticObjects.Foliage if state then for _, tree in ipairs(replicatedStorage:FindFirstChild("Trees"):GetChildren()) do tree.Parent = trees end else local storedTrees = Instance.new("Folder") storedTrees.Name = "Trees" storedTrees.Parent = replicatedStorage for _, tree in ipairs(trees:GetChildren()) do if tree.Name == "Tree" then tree.Parent = storedTrees end end end end)end})
YOURTABHERE:AddToggle('Grass', {Text = 'Grass',Default = true,Callback = function(state) if state then local showDecorations = true sethiddenproperty(game:GetService("Workspace").Terrain, "Decoration", showDecorations) else local showDecorations = false sethiddenproperty(game:GetService("Workspace").Terrain, "Decoration", showDecorations) end end})
